
public class chapter2_09 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int temp;
		
		int x = 10;
		int y = 20;
		System.out.println("x = " + x);
		System.out.println("y = " + y);
		
		temp = x;
		x = y;
		y = temp;
		System.out.println("x = " + x);
		System.out.println("y = " + y);
	}

}
